п»їРїВ»С—Р С—Р’В»РЎвЂ”Р В РЎвЂ”Р вЂ™Р’В»Р РЋРІР‚вЂќР В РЎвЂ”Р вЂ™Р’В»Р РЋРІР‚вЂќ<# =======================================================================
  CheCha CONTROL PANEL Р В Р’В Р В РІР‚В Р В Р’В Р Р†Р вЂљРЎв„ўР В Р вЂ Р В РІР‚С™Р РЋРЎС™ Interactive Console Menu
  Author: Р В Р’В Р вЂ™Р’В Р В Р’В Р В РІР‚в„–.Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В§. (CheCha)
  Path:   C:\CHECHA_CORE\C11\C11_AUTOMATION\tools\Checha-ControlPanel.ps1
  Requires: PowerShell 7+
======================================================================= #>

#requires -Version 7.0

param(
  [string]$ChechaRoot = "C:\CHECHA_CORE",
  [switch]$Force
)

[Console]::OutputEncoding = [System.Text.Encoding]::UTF8
$ErrorActionPreference = "Stop"
$script:Force = [bool]$Force

# --- Paths ---------------------------------------------------------------
$Paths = [ordered]@{
  Root                = $ChechaRoot
  C03_Log             = Join-Path $ChechaRoot "C03\LOG"
  RhythmDash          = Join-Path $ChechaRoot "RHYTHM_DASHBOARD.md"
  RunAll              = Join-Path $ChechaRoot "C11\C11_AUTOMATION\Checha-RunAll.ps1"
  Pipelines           = Join-Path $ChechaRoot "C11\C11_AUTOMATION\Run-ChechaPipelines.ps1"
  PanelContext        = Join-Path $ChechaRoot "C11\C11_AUTOMATION\tools\Add-CheChaContext.ps1"
  VaultDashScript     = Join-Path $ChechaRoot "C11\C11_AUTOMATION\tools\Update-VaultDashboard.ps1"
  ShelvesConfig       = Join-Path $ChechaRoot "C11\C11_AUTOMATION\config\checha_shelves.json"
  VaultRoot           = Join-Path $ChechaRoot "C12\Vault"
  StrategAgentDir     = Join-Path $ChechaRoot "C11\C11_AUTOMATION\AGENTS\G44_STRATEG"
  VerifyRelease       = Join-Path $ChechaRoot "tools\verify_release_assets.ps1"
  CheckRelease        = Join-Path $ChechaRoot "tools\check_release.ps1"
  Protocols           = Join-Path $ChechaRoot "C12\Protocols"
  ControlPanelMD      = Join-Path $ChechaRoot "C06_FOCUS\CONTROL_PANEL.md"
  # G04
  G04Dir              = Join-Path $ChechaRoot "C11\C11_AUTOMATION\G04"
  G04Escalation       = Join-Path $ChechaRoot "C11\C11_AUTOMATION\G04\Handle-G04-Escalation.ps1"
  # Incidents
  IncidentsRoot       = Join-Path $ChechaRoot "C12\Vault\Incidents"
  # Strategic Reports
  StrategicReadme     = Join-Path $ChechaRoot "C12\Vault\StrategicReports\README.md"
  StrategicIndex      = Join-Path $ChechaRoot "C12\Vault\StrategicReports\_index.md"
  # Icons
  ShortcutIconDefault = Join-Path $ChechaRoot "C11\C11_AUTOMATION\tools\assets\checha_dao.ico"
}

# --- Transcript ----------------------------------------------------------
try{
  if(-not (Test-Path $Paths.C03_Log)){ New-Item -ItemType Directory -Path $Paths.C03_Log | Out-Null }
  $global:__ChechaTranscript = Join-Path $Paths.C03_Log ("control_panel_{0}.log" -f (Get-Date -Format "yyyyMMdd_HHmmss"))
  Start-Transcript -Path $global:__ChechaTranscript -Append -ErrorAction SilentlyContinue | Out-Null
} catch {}

# --- Tasks & Agents maps -------------------------------------------------
$Tasks = @{
  GitSync       = "ChechaGitSync"
  StrategWeekly = "Checha-Agent-Strateg-Weekly"
}
$Agents = [ordered]@{
  "G44 Strategic (task)"   = @{ Type="task";   Name=$Tasks.StrategWeekly }
  "G04 Coordination dir"   = @{ Type="open";   Path=(Join-Path $ChechaRoot "C11\C11_AUTOMATION\AGENTS\G04_COORD") }
  "G11 Leadership dir"     = @{ Type="open";   Path=(Join-Path $ChechaRoot "C11\C11_AUTOMATION\AGENTS\G11_LEAD") }
  "G04 Escalation (script)"= @{ Type="script"; Path=$Paths.G04Escalation }
}

# --- Panel state ---------------------------------------------------------
$StatePath = Join-Path $ChechaRoot "C11\C11_AUTOMATION\state\panel_state.json"
function Get-State(){ if(Test-Path $StatePath){ try{ Get-Content $StatePath -Raw | ConvertFrom-Json }catch{ [pscustomobject]@{} } } else { [pscustomobject]@{} } }
function Save-State($obj){
  $dir = Split-Path $StatePath -Parent
  if(-not (Test-Path $dir)){ New-Item -ItemType Directory -Path $dir | Out-Null }
  ($obj | ConvertTo-Json -Depth 6) | Set-Content -Path $StatePath -Encoding UTF8
}
function Prompt-Tag([string]$hint){
  $st = Get-State
  $def = $st.lastTag
  if($def){ Write-Host "[i] Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎвЂќР В Р’В Р В Р вЂ№Р В Р’В Р РЋРІР‚СљР В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРІвЂћСћР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р В Р вЂ№-Р В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦- Р В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРІвЂћСћР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’ВµР В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦-: $def" -ForegroundColor DarkGray }
  $tag = Read-Host $hint
  if([string]::IsNullOrWhiteSpace($tag)){ return $null }
  $st.lastTag = $tag; Save-State $st
  return $tag
}

# --- Helpers -------------------------------------------------------------
function Write-Info($m){ Write-Host "[i] $m" -ForegroundColor Cyan }
function Write-Ok($m){ Write-Host "[OK] $m" -ForegroundColor Green }
function Write-Warn($m){ Write-Host "[WARN] $m" -ForegroundColor Yellow }
function Write-Err($m){ Write-Host "[ERR] $m" -ForegroundColor Red }

function Assert-File([string]$p){ if(-not (Test-Path $p -PathType Leaf)){ throw "Р В Р’В Р вЂ™Р’В Р В Р Р‹Р РЋРЎв„ўР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’Вµ Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В·Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦-Р В Р’В Р вЂ™Р’В Р В РЎС›Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’ВµР В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС› Р В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРІР‚С”Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦-Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В»: $p" } }
function Assert-Dir([string]$p){ if(-not (Test-Path $p -PathType Container)){ throw "Р В Р’В Р вЂ™Р’В Р В Р Р‹Р РЋРЎв„ўР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’Вµ Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В·Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦-Р В Р’В Р вЂ™Р’В Р В РЎС›Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’ВµР В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС› Р В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРІвЂћСћР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’ВµР В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎСљР В Р’В Р В Р вЂ№Р В Р Р‹Р Р†Р вЂљРЎС™: $p" } }

function Open-Path([string]$p){
  if(Test-Path $p){ try{ Start-Process $p | Out-Null }catch{ Write-Warn "Р В Р’В Р вЂ™Р’В Р В Р Р‹Р РЋРЎв„ўР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’Вµ Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В Р В Р’В Р вЂ™Р’В Р В РЎС›Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В»Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС›Р В Р’В Р В Р вЂ№Р В Р’В Р РЋРІР‚СљР В Р’В Р В Р вЂ№Р В Р’В Р В Р РЏ Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В Р В Р’В Р В Р вЂ№-Р В Р’В Р вЂ™Р’В Р В РЎС›Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎСљР В Р’В Р В Р вЂ№Р В Р’В Р Р†Р вЂљРЎв„ўР В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљР’ВР В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРІвЂћСћР В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљР’В: $p Р В Р’В Р В РІР‚В Р В Р’В Р Р†Р вЂљРЎв„ўР В Р вЂ Р В РІР‚С™Р РЋРЎС™ $($_.Exception.Message)" } }
  else{ Write-Warn "Р В Р’В Р вЂ™Р’В Р В Р’В Р В РЎвЂњР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В»Р В Р’В Р В Р вЂ№Р В Р’В Р В Р РЏР В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р вЂ™Р’В¦ Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’Вµ Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В·Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦-Р В Р’В Р вЂ™Р’В Р В РЎС›Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’ВµР В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС›: $p" }
}

function Tail-LastLog([int]$Lines=60){
  Assert-Dir $Paths.C03_Log
  $last = Get-ChildItem $Paths.C03_Log -File | Sort-Object LastWriteTime -Desc | Select-Object -First 1
  if($last){
    Write-Info "Р В Р’В Р вЂ™Р’В Р В Р вЂ Р В РІР‚С™Р РЋРІР‚СњР В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС›Р В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦-: $($last.FullName)"
    Get-Content $last.FullName -Tail $Lines
  } else { Write-Warn "Р В Р’В Р вЂ™Р’В Р В Р вЂ Р В РІР‚С™Р РЋРІР‚СњР В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС›Р В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦-Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљР’В Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В Р В Р’В Р В Р вЂ№-Р В Р’В Р вЂ™Р’В Р В РЎС›Р Р†Р вЂљР’ВР В Р’В Р В Р вЂ№Р В Р’В Р РЋРІР‚СљР В Р’В Р В Р вЂ№Р В Р Р‹Р Р†Р вЂљРЎС™Р В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРІвЂћСћР В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р В Р вЂ№- Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В  $($Paths.C03_Log)" }
}

function Run-PS([string]$ScriptPath, [hashtable]$Args){
  Assert-File $ScriptPath
  $sb = { param($sp, $argsHash) & $sp @argsHash; if($LASTEXITCODE -ne $null -and $LASTEXITCODE -ne 0){ throw "ExitCode=$LASTEXITCODE" } }
  & pwsh -NoProfile -ExecutionPolicy Bypass -Command $sb -Args $ScriptPath, $Args
  Write-Ok "Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†РІР‚С›РЎС›Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС›Р В Р’В Р вЂ™Р’В Р В Р Р‹Р вЂ™Р’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РЎС›Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В° Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎСљР В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС›Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°."
}

function Exists-Task([string]$name){ $null = & schtasks /Query /TN $name 2>$null; return ($LASTEXITCODE -eq 0) }
