  if([string]::IsNullOrWhiteSpace($inp)){
    if(Test-Path $Paths.ShortcutIconDefault -PathType Leaf){ Set-IconPath $Paths.ShortcutIconDefault } else { Write-Warn "Р В Р’В Р вЂ™Р’В Р В Р вЂ Р В РІР‚С™Р РЋРЎС™Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’ВµР В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРІР‚С”Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС›Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В»Р В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРІвЂћСћР В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС›Р В Р’В Р В Р вЂ№- .ico Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’ВµР В Р’В Р вЂ™Р’В Р В Р Р‹Р вЂ™Р’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р В Р вЂ№Р В Р вЂ Р В РІР‚С™Р РЋРЎС™." }
  } elseif($inp.Trim().ToLower() -eq "clear"){ Set-IconPath $null }
  else { Set-IconPath $inp }
}

# --- PS 5.1 STA helpers --------------------------------------------------
function Ensure-PS5Path(){ $ps5=Join-Path $env:WINDIR "System32\WindowsPowerShell\v1.0\powershell.exe"; if(-not (Test-Path $ps5 -PathType Leaf)){ throw "Р В Р’В Р вЂ™Р’В Р В Р Р‹Р РЋРЎв„ўР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’Вµ Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В·Р В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В°Р В Р’В Р вЂ™Р’В Р В РЎвЂ”Р РЋРІР‚вЂќР В РІР‚В¦-Р В Р’В Р вЂ™Р’В Р В РЎС›Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’ВµР В Р’В Р вЂ™Р’В Р В Р’В Р Р†Р вЂљР’В¦Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљРЎС› Windows PowerShell 5.1: $ps5" }; $ps5 }
function New-ShortcutSTA {
  param([Parameter(Mandatory)][string]$Path,[Parameter(Mandatory)][string]$Target,[Parameter(Mandatory)][string]$WorkDir,[string]$Icon)
  $PS5 = Ensure-PS5Path
  $tmp = Join-Path $env:TEMP ("mklnk_{0}.ps1" -f ([guid]::NewGuid().ToString('N')))
  @'
param([string]$Path,[string]$Target,[string]$WorkDir,[string]$Icon)
$ErrorActionPreference='Stop'
$shell = New-Object -ComObject WScript.Shell
$dir = Split-Path $Path -Parent
if(-not (Test-Path $dir)){ New-Item -ItemType Directory -Path $dir -Force | Out-Null }
if(Test-Path $Path){
  try{ (Get-Item $Path).Attributes = 'Normal' }catch{}
  try{ Remove-Item $Path -Force -ErrorAction SilentlyContinue }catch{}
}
$lnk = $shell.CreateShortcut($Path)
$lnk.TargetPath       = $Target
$lnk.Arguments        = ""
$lnk.WorkingDirectory = $WorkDir
$lnk.WindowStyle      = 1
if($Icon -and (Test-Path $Icon)){ $lnk.IconLocation = "$Icon, 0" }
$lnk.Save()
if(-not (Test-Path $Path)){ throw "Save() completed but file not found: $Path" }
try{ (Get-Item $Path).Attributes = 'ReadOnly' }catch{}
try{ attrib +P $Path 2>$null }catch{}
'@ | Set-Content -Path $tmp -Encoding UTF8
  & $PS5 -STA -NoProfile -File $tmp -Path $Path -Target $Target -WorkDir $WorkDir -Icon $Icon | Out-Null
  $code=$LASTEXITCODE
  Remove-Item $tmp -Force -ErrorAction SilentlyContinue
  if(-not (Test-Path $Path)){ throw "Failed to create shortcut: $Path (exit $code)" }
}

