function Action-ShortcutLogMenu(){
  Write-Host "18.1) Open in Notepad"
  Write-Host "18.2) Tail (live view)"
  Write-Host "18.3) Clear log"
  Write-Host "0) Back"
  $s = Read-Host "Р В Р’В Р вЂ™Р’В Р В Р вЂ Р В РІР‚С™Р Р†РІР‚С›РЎС›Р В Р’В Р вЂ™Р’В Р В Р Р‹Р Р†Р вЂљР’ВР В Р’В Р вЂ™Р’В Р В РІР‚в„ўР вЂ™Р’В±Р В Р’В Р В Р вЂ№-Р В Р’В Р В Р вЂ№Р В Р’В Р Р†Р вЂљРЎв„ў"
