function Show-Header(){
  Clear-Host
  Write-Host ""
  Write-Host "==================== Р В Р Р‹Р В РІР‚С™Р В Р Р‹Р РЋРЎСџР В Р вЂ Р В РІР‚С™Р РЋРІР‚СњР В РІР‚в„ўР вЂ™Р’В° CheCha CONTROL PANEL ====================" -ForegroundColor Magenta
  Write-Host ("  Root: {0}" -f $ChechaRoot) -ForegroundColor DarkGray
